import React, { useState } from "react";
import ReactDOM from "react-dom";
import cmd from  "./cmd.sass";


class Cmd extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            input: '',
            output: '',
            cmd: -1
          }

        this.setInput = this.setInput.bind(this);
    }

    setInput(e){
        const saveValue = e.target.value;
        this.setState({
            input: saveValue
        });
        console.log(saveValue);

    }

    componentDidMount(){
        return console.log("cmd montée")
    }

    render() {
        return (
            <div id="Cmd" className="">
                <div id="Cmd-text-area" class="text-area">
                    <code id="Cmd-Input" className="">
                        <input className="cmd-input" type="text" onMouseEnter={this.setInput} placeholder="Write a command">
                        </input>
                    </code>
                    <br />
                    <code  id="Cmd-Output" className="output"></code>

                </div>
            </div>
        );


    }
};


export default Cmd;
