import React, { useState } from "react";
import ReactDOM from "react-dom";
import cmd from  "./cmd.sass";

class Input extends React.Component {
    _handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        setInput(e);
      }
    }

    render() {
      return <input className="cmd-input" type="text" onKeyDown={this._handleKeyDown} />
    }
  }



class Cmd extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            input: '',
            output: '',
            cmd: -1
          }

        this.setInput = this.setInput.bind(this);
    }

    setInput(e){
        const saveValue = e.target.value;
        this.setState({
            input: saveValue
        });
        console.log(saveValue);

    }

    componentDidMount(){
        return console.log("cmd montée")
    }



    render() {
        return (
            <div id="Cmd" className="">
                <div id="Cmd-text-area" class="text-area">
                    <code id="Cmd-Input" className="">
                        <Input className="cmd-input" type="text" onChange={this.setInput} placeholder="Write a command">
                        </Input>
                    </code>
                    <br />
                    <code  id="Cmd-Output" className="output">{this.state.input}</code>

                </div>
            </div>
        );


    }
};


export default Cmd;
